package com.Training;

public class Jpr7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 /* Arithmatic operators */
		int result=1;
		result+=2;
		System.out.println(result);// result is 3
		result--;
		System.out.println(result);// result value is 2
		result*=2;
		System.out.println(result);// result value is 4
		result/=2;
		System.out.println(result);// result value is 2
		result+=8;
		System.out.println(result);// result value is 10
		result%=7;
		System.out.println(result);// result value is 3


	}

}
